import styled from "styled-components";
import { animated } from "react-spring";

export const OverlayContainer = styled(animated.div)`
  position: fixed;
  top: 0;
  right: 0;
  left: 0;
  z-index: 9999;
  width: 100vw;
  height: 100vh;
  background: #e5edec;
  overflow: scroll;
`;
export const OverlayCloseIcon = styled(animated.img)`
  position: absolute;
  z-index: 10000;
  height: 45px;
  width: 45px;
  top: 35px;
  right: 35px;
  cursor: pointer;
`;

export const OverlayContent = styled.div`
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2em;
  padding-top: 70px;
`;

export const OverlayHeaders = styled.div``;

export const OverlayNumber = styled.span`
  font-size: 2.4rem;
  letter-spacing: 0.5px;
  color: #a9aaaa;
`;

export const OverlayName = styled.p`
  display: inline;
  font-size: 2rem;
  letter-spacing: 1px;
  color: #454747;
  margin-left: 10px;
`;

export const OverlayKanji = styled.p`
  font-size: 4.5rem;
  color: #5c5f5e;
`;

export const OverlayTypes = styled.div`
  margin-top: 10px;
`;

export const OverlayType = styled.span`
  font-size: 2rem;
  padding: 0.2em 1em;
  border-radius: 5px;
  background-color: #e9e3a3;
`;

export const OverlayImg = styled.img`
  width: 100%;
  max-width: 310px;
`;

export const OverlayFooter = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-top: 10px;
`;

export const OverlayAddButton = styled(animated.img)`
  width: 36px;
  height: 36px;
  margin-bottom: 20px;
`;

export const OverlayStats = styled.div``;

export const OverlayStat = styled.span``;
