import React from "react";
import { useSpring, animated } from "react-spring";
import {
  SlideContainer,
  SlideImg,
  SlideContent,
  SlideNumber,
  SlideName,
  SlideKanji,
  SlideTypes,
  SlideType,
  SearchAsset,
} from "./SlideElements";

import searchImg from "../../assets/images/searchImg.png";

const Slide = ({ img, number, name, kanji, types }) => {
  const fade = useSpring({
    from: {
      opacity: 0,
    },
    opacity: 1,
  });

  return (
    <SlideContainer style={fade}>
      <SlideImg src={img} />
      <SlideContent>
        <SlideNumber>
          {number} <SlideName>{name}</SlideName>
        </SlideNumber>
        <SlideKanji>{kanji}</SlideKanji>
        <SlideTypes>
          <SlideType>psychic</SlideType>
        </SlideTypes>
      </SlideContent>
      <SearchAsset src={searchImg} />
    </SlideContainer>
  );
};

export default Slide;
