import styled from "styled-components";
import { animated } from "react-spring";

export const OverlayContainer = styled(animated.div)`
  position: fixed;
  top: 0;
  right: 0;
  left: 0;
  z-index: 9999;
  width: 100vw;
  height: 100vh;
  background: #e5edec;
`;
export const OverlayCloseIcon = styled(animated.img)`
  position: absolute;
  z-index: 10000;
  height: 45px;
  width: 45px;
  top: 0;
  right: 0;
  left: 0;
  background: red;
`;
