import React, { useState } from "react";
import { useSpring } from "react-spring";
import { SliderContainer, LeftArrow, RightArrow } from "./SliderElements";
import Slide from "../Slide";
import cosmog from "../../assets/images/cosmog.png";
import corsola from "../../assets/images/corsola.png";
import bewear from "../../assets/images/bewear.png";
import arrLeft from "../../assets/images/arrw-l.png";
import arrRight from "../../assets/images/arrw-r.png";

const Hero = () => {
  const [isAnimating, setIsAnimating] = useState(false);

  const [{ x }, set] = useSpring(() => ({ x: [0, 0] }));

  return (
    <SliderContainer>
      <Slide
        img={cosmog}
        number="#789"
        name="Cosmog"
        kanji="コスモッグ"
        bgColor="#EEEC7F"
        style={{
          transform: x.interpolate(
            (value) => `$translate3d(${value}, 0px 0px)`
          ),
        }}
      />
      <Slide
        img={corsola}
        number="#864"
        name="Corsola"
        kanji="サニーゴ"
        bgColor="#8F8F90"
      />
      <Slide
        img={bewear}
        number="#760"
        name="Bewear"
        kanji="キテルグマ"
        bgColor="#EDA5B3"
      />
      <LeftArrow arrow={arrLeft} />
      <RightArrow arrow={arrRight} />
    </SliderContainer>
  );
};

export default Hero;
