import React, { useState } from "react";
import {
  CardContainer,
  CardImg,
  CardName,
  CardTypes,
  CardType,
} from "./PokemonCardElements";
import { pokemonTypes } from "../../data/PokemonData";
import PokemonOverlay from "../PokemonOverlay";
const PokemonCard = ({ name, types, img }) => {
  const [toggle, setToggle] = useState(false);

  return toggle ? (
    <PokemonOverlay />
  ) : (
    <CardContainer onClick={() => setToggle(!toggle)}>
      <CardImg src={img} />
      <CardName>{name}</CardName>
      <CardTypes>
        {types.map((type) => (
          <CardType
            key={type}
            textColor={pokemonTypes[type].color}
            bgcolor={pokemonTypes[type].bgColor}
          >
            {type}
          </CardType>
        ))}
      </CardTypes>
    </CardContainer>
  );
};

export default PokemonCard;
