import styled from "styled-components";
import { animated } from "react-spring";

export const DashboardContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: 120px 2fr 80px;
  justify-content: center;
  align-items: center;
  padding: 2em;
`;

export const DashboardHeaders = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

export const ShowTeam = styled.div`
  width: 280px;
  display: flex;
  align-items: center;
  order: 3;
  background: #fafafafa;
  border-radius: 0px 10px 10px 0px;
  padding: 10px;
`;

export const TeamHeading = styled.h3`
  font-weight: var(--font-weight-bold);
  font-size: 1.8rem;
  letter-spacing: 1px;
  color: rgba(0, 0, 0, .7);
  margin-right: 10px;
`;

export const TeamIcon = styled(animated.img)`
  width: 26px;
  height: 26px;

  &:hover {
    transform: scale(1.3);
  }
`;

export const FilterContainer = styled.div`
  width: 280px;
  display: flex;
`;

export const FilterButton = styled(animated.button)`
`;

export const FilterIcon = styled.img`
  width: 27px;
  height: 27px;
`;

export const SearchContainer = styled.div``;

export const SearchInput = styled.input``;

export const SearchIcon = styled.img``;
