import styled from "styled-components";

export const HeroContainer = styled.div`
  position: relative;
  width: 100vw;
  height: 100vh;
  max-height: 1100px;
  display: flex;
  overflow: hidden;
`;

export const LeftArrow = styled.button`
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  width: 50%;
  height: 100%;
  cursor: url(${(props) => props.arrow}), auto;
`;

export const RightArrow = styled.button`
  display: block;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  width: 50%;
  height: 100%;
  cursor: url(${(props) => props.arrow}), auto;
`;
