import styled from "styled-components";
import { animated } from "react-spring";

export const OverlayContainer = styled(animated.div)`
  position: fixed;
  z-index: 9999999;
  top: 0;
  right: 0;
  left: 0;
  z-index: 9999;
  width: 100%;
  height: 100%;
  background: #e5edec;
  overflow: scroll;
`;
export const OverlayCloseIcon = styled(animated.img)`
  position: absolute;
  z-index: 10000;
  height: 45px;
  width: 45px;
  top: 35px;
  right: 35px;
  cursor: pointer;
`;

export const OverlayContent = styled.div`
  ${'' /* min-height: 100%; */}
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 2em;
  padding-top: 250px;

  @media screen and (min-width: 441px) {
    padding-top: 300px;
  }
`;

export const OverlayHeaders = styled.div``;

export const OverlayNumber = styled.span`
  font-size: 2.4rem;
  letter-spacing: 0.5px;
  color: #a9aaaa;
`;

export const OverlayName = styled.p`
  display: inline;
  font-size: 2.1rem;
  letter-spacing: 1px;
  color: #454747;
  margin-left: 10px;

  @media screen and (min-width: 441px) {
    font-size: 2.4rem;
  }
`;

export const OverlayKanji = styled.p`
  font-size: 4.5rem;
  color: #5c5f5e;

  @media screen and (min-width: 441px) {
    font-size: 5.4rem;
  }
`;

export const OverlayTypes = styled.div`
  margin-top: 10px;
`;

export const OverlayType = styled.span`
  font-size: 2rem;
  padding: 0.2em 1em;
  border-radius: 5px;
  background-color: #e9e3a3;
`;

export const OverlayImg = styled.img`
  width: 100%;
  max-width: 310px;
`;

export const OverlayFooter = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-top: 10px;
`;

export const OverlayAddButton = styled(animated.img)`
  margin-top: 15px;
  width: 36px;
  height: 36px;
  cursor: pointer;
`;

export const OverlayStats = styled.div`
  margin-top: 25px;
  width: 100%;
  max-width: 320px;
  display: grid;
  grid-template-columns: 1fr 1fr;
  padding-bottom: 30px;
  justify-content: center;
  justify-items: center;
  align-items: center;
  ${"" /* background: lightgrey; */}
`;

export const OverlayStat = styled.p`
  font-size: 1.8rem;
  background-color: #fff;
  color: #4d4d4d;
  padding: 5px 10px;
  padding-right: 15px;
  margin: 5px 0;
  border-radius: 10px;
  justify-self: ${({ left }) => (left ? "flex-start" : "center")};
`;
