import React, { useState } from "react";
import {
  SlideContainer,
  SlideImg,
  SlideContent,
  SlideNumber,
  SlideName,
  SlideKanji,
  SlideTypes,
  SlideType,
  SearchAsset,
  LeftArrow,
  RightArrow,
} from "./SlideElements";
import arrLeft from "../../assets/images/arrw-l.png";
import arrRight from "../../assets/images/arrw-r.png";
import searchImg from "../../assets/images/searchImg.png";

const Slide = ({ img, number, name, kanji, types }) => {
  const [slideToggled, setSlideToggled] = useState("");

  return (
    <SlideContainer>
      <SlideImg src={img} />
      <SlideContent>
        <SlideNumber>
          {number} <SlideName>{name}</SlideName>
        </SlideNumber>
        <SlideKanji>{kanji}</SlideKanji>
        <SlideTypes>
          <SlideType>psychic</SlideType>
        </SlideTypes>
      </SlideContent>
      <SearchAsset src={searchImg} />
      <LeftArrow onClick={() => setSlideToggled("prev")} arrow={arrLeft} />
      <RightArrow onClick={() => setSlideToggled("right")} arrow={arrRight} />
    </SlideContainer>
  );
};

export default Slide;
