import React, { useState } from "react";
import {
  DashboardContainer,
  DashboardHeaders,
  ShowTeam,
  TeamHeading,
  TeamIcon,
  FilterContainer,
  FilterButton,
  FilterIcon,
  SearchContainer,
  SearchInput,
  SearchIcon,
  DashboardContent,
} from "./Dashboard";
import PokemonCard from "../PokemonCard";
import PokemonOverlay from "../PokemonOverlay";
import pokeball from "../../assets/images/pokeball.png";
import filterIcon from "../../assets/images/filterIcon.png";
import searchIcon from "../../assets/images/searchIcon.png";

import { pokemonList } from "../../data/PokemonData";

const Dashboard = () => {
  const [isOverlayOpen, setIsOverlayOpen] = useState(false);

  const toggleOverlay = () => setIsOverlayOpen(!isOverlayOpen);
  return (
    <DashboardContainer id="search">
      <DashboardHeaders>
        <ShowTeam>
          <TeamHeading>Team: </TeamHeading>
          <TeamIcon src={pokeball} />
        </ShowTeam>
        <FilterContainer>
          <FilterButton>show all filters</FilterButton>
          <FilterIcon src={filterIcon} />
        </FilterContainer>
        <SearchContainer>
          <SearchInput placeholder="search..." />
          <SearchIcon src={searchIcon} />
        </SearchContainer>
      </DashboardHeaders>
      <DashboardContent>
        {pokemonList.map(({ name, types, img }) => (
          <PokemonCard
            key={name}
            name={name}
            types={types}
            img={img}
            toggleOverlay={toggleOverlay}
          />
        ))}
      </DashboardContent>
      {isOverlayOpen && <PokemonOverlay />}
    </DashboardContainer>
  );
};

export default Dashboard;
