import React, { useState } from "react";
import { HeroContainer, LeftArrow, RightArrow } from "./HeroElements";
import Slide from "../Slide";
import arrLeft from "../../assets/images/arrw-l.png";
import arrRight from "../../assets/images/arrw-r.png";
import cosmog from "../../assets/images/cosmog.png";

const Hero = () => {
  const [btnClick, setBtnClick] = useState("");

  const slideToggled = (direction) => {
    setBtnClick(direction)
  }

  return (
    <HeroContainer>
      <Slide img={cosmog} number="#789" name="Cosmog" kanji="コスモッグ" />
      <Slide img={cosmog} number="#DELETE" name="DELETE" kanji="DELETE" />
      <LeftArrow onClick={slideToggled("left")} arrow={arrLeft} />
      <RightArrow arrow={arrRight} />
    </HeroContainer>
  );
};

export default Hero;
