import React, { useState } from "react";
import { HeroContainer, LeftArrow, RightArrow } from "./HeroElements";
import Slide from "../Slide";
import cosmog from "../../assets/images/cosmog.png";
import corsola from "../../assets/images/corsola.png";
import bewear from "../../assets/images/bewear.png";
import arrLeft from "../../assets/images/arrw-l.png";
import arrRight from "../../assets/images/arrw-r.png";

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const slideLength = 3;

  const nextSlide = () => {
    setCurrentSlide(currentSlide === -1 ? 3 : currentSlide + 1);
  };

  return (
    <HeroContainer>
      <Slide
        img={cosmog}
        number="#789"
        name="Cosmog"
        kanji="コスモッグ"
        bgColor="#EEEC7F"
      />
      <Slide
        img={corsola}
        number="#864"
        name="Corsola"
        kanji="サニーゴ"
        bgColor="#8F8F90"
      />
      <Slide
        img={bewear}
        number="#760"
        name="Bewear"
        kanji="キテルグマ"
        bgColor="#EDA5B3"
      />
      <LeftArrow arrow={arrLeft} onClick={nextSlide()} />
      <RightArrow arrow={arrRight} />
    </HeroContainer>
  );
};

export default Hero;
